mapper.select:
SELECT my_need_like.id as id, my_need_like.content as content, my_need_like.title as title, my_need_like.like_num as likeNum, my_need_like.picture as picture, my_need_like.publish_status as publishStatus, my_need_like.valid_time as validTime, my_need_like.create_date as createDate, my_need_like.video as video from my_need_like where del!=1 AND valid_time > NOW()
and my_need_like.title like concat('%', #{title}, '%')
AND valid_time >= #{validStartTime}
AND valid_time <= #{validStopTime}
AND id IN ( SELECT l.like_conten_id FROM like_info l WHERE del!=1 AND l.like_time >= #{likeStartTime} AND l.like_time <= #{likeStopTime} GROUP BY like_conten_id )
AND publish_status = #{publishStatus}
ORDER BY create_date desc

SELECT COUNT(1) FROM my_need_like
WHERE del =  '0'
AND title = #{title}

    AND id != #{id}

mapper.delete:
UPDATE my_need_like
SET del = 1
WHERE id = #{id}

UPDATE my_need_like SET del = 1
WHERE id IN

    #{i}

mapper.update:
UPDATE my_need_like  SET
publish_status = '1'
WHERE id = #{id}

UPDATE my_need_like m SET
m.publish_status = '0'
WHERE m.id = #{id}
