mapper.select:
SELECT id as id, icon as icon, comment as comment, like_p_id as likePID, like_conten_id as likeContentId, like_time as likeTime, like_c_num as likeCNum from like_info where del!=1 and comment != ''
and like_conten_id = #{likeContentId}
ORDER BY create_date desc

mapper.delete:
UPDATE like_info
SET del = 1
WHERE id = #{id}
