mapper.select:
SELECT COUNT(1) FROM qgq_news
WHERE del =  '0'
AND `title` = #{title}

    AND id != #{id}

SELECT COUNT(1) FROM oa_post
WHERE del = '0'
AND identification = #{identification}

    AND id != #{id}

mapper.delete:
UPDATE qgq_news
SET del = 1
WHERE id = #{id}

UPDATE qgq_news SET del = 1
WHERE id IN

    #{i}

mapper.update:
UPDATE qgq_news
SET status = '已发布状态',news_date = now()
WHERE id = #{id}

UPDATE qgq_news
SET status = '未发布状态',news_date = null
WHERE id = #{id}
