mapper.update:
update jacg_warning t1 set
t1.warningPosition = #{warningPosition},
t1.description = #{description},
t1.update_date = #{updateDate},
t1.update_user = #{updateUser.id}

where t1.id=#{id}

mapper.delete:
UPDATE jacg_warning
SET del = 1
WHERE id = #{id}

UPDATE jacg_warning SET del = 1
WHERE id IN

    #{i}

mapper.select:
SELECT t1.id id, t1.warningPosition warningPosition, t1.`description` description
FROM `jacg_warning` t1
where t1.id=#{id}
