mapper.delete:
UPDATE jacg_weixin_queinvest
SET del = 1
WHERE id = #{id}

UPDATE jacg_weixin_queinvest SET del = 1 WHERE id IN
#{i}
and status != '2'

mapper.select:
SELECT t1.id id, t1.title title, t1.`description` description, DATE_FORMAT( t1.valid, '%Y-%m-%d' ) valid, t1.queinvestType queinvestType, t1.status status, DATE_FORMAT( t1.pubdate, '%Y-%m-%d %T' ) pubdate
FROM jacg_weixin_queinvest t1
where t1.id=#{id}

SELECT t1.id id, t1.title title, t1.`description` description, t1.queinvestType queinvestType
FROM `jacg_weixin_queinvest` t1
where t1.id=#{id}
