mapper.delete:
UPDATE jacg_weixin_questions
SET del = 1
WHERE id = #{id}

UPDATE jacg_weixin_questions SET del = 1
WHERE id IN

#{i}

mapper.select:
select
t1.id id, t1.contents contents, t1.questionType questionType, t2.choiceText01 choiceText01, t2.choiceText02 choiceText02, t2.choiceText03 choiceText03, t2.choiceText04 choiceText04
from jacg_weixin_questions t1 left join jacg_weixin_options t2 on t1.id = t2.questions_id where t1.id=#{id}
