mapper.select:
SELECT COUNT(1) FROM oa_post
WHERE del =  '0'
AND `name` = #{name}

    AND id != #{id}

SELECT COUNT(1) FROM oa_post
WHERE del = '0'
AND identification = #{identification}

    AND id != #{id}

mapper.update:
UPDATE oa_post m SET
m.name = #{name},
m.mark = #{mark},
m.update_date = #{updateDate},
m.update_user = #{updateUser.id}

WHERE m.id = #{id}

mapper.delete:
UPDATE oa_post
SET del = 1
WHERE id = #{id}

UPDATE oa_post SET del = 1
WHERE id IN

    #{i}
